package com.example.buyunick.Activitys;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.app.TaskStackBuilder;
import androidx.recyclerview.widget.DividerItemDecoration;
import androidx.recyclerview.widget.LinearLayoutManager;

import android.content.Context;
import android.os.Bundle;

import com.example.buyunick.R;
import com.example.buyunick.adopter.Cart_Adapter;
import com.example.buyunick.databinding.ActivityCardBinding;
import com.example.buyunick.modals.product;

import java.util.ArrayList;
import java.util.Objects;

public class CardActivity extends AppCompatActivity {

    ActivityCardBinding binding;
    Cart_Adapter Adapter;
    ArrayList<product> Product;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        binding = ActivityCardBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());

        Product = new ArrayList<>();

        Product.add(new product("t shirt", "data:image/jpeg;base64,", "Good Quality", 120, 10, 5,2));
        Product.add(new product("t shirt", "data:image/jpeg;base64,", "Good Quality", 120, 10, 5,2));
        Product.add(new product("t shirt", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTYklU9G0MK_xY3UOAfWwffT3USBSlCAxneaw&usqp=CAU", "Good Quality", 120, 10, 5,2));
        Product.add(new product("t shirt", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ40bnhmOIFdOM5kLP13EF1X6R-yjrQBqCI3w&usqp=CAU", "Good Quality", 120, 10, 5,2));
        Product.add(new product("t shirt", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQohi441xItIVq_YuwU9vHpi0GTp9mAB4yRyQ&usqp=CAU", "Good Quality", 120, 10, 5,2));

        Adapter = new Cart_Adapter(this, Product);


        LinearLayoutManager leyoutmaneger = new LinearLayoutManager(this);
        DividerItemDecoration decoretion = new DividerItemDecoration(this, leyoutmaneger.getOrientation());
        binding.CartList.setLayoutManager(leyoutmaneger);
        binding.CartList.addItemDecoration(decoretion);
        binding.CartList.setAdapter(Adapter);

        Objects.requireNonNull(getSupportActionBar()).setDisplayHomeAsUpEnabled(true);

    }

    @Override
    public boolean onSupportNavigateUp() {
        finish();
        return super.onSupportNavigateUp();
    }
}
